import React, { useEffect, useState } from "react";
import {Container, Card, Card3} from '../Styles/Style';
import { Doughnut } from 'react-chartjs-2';
import axios from "axios";



// O gráfico de incidentes deve exibir um gráfico de pizza com o nome das categorias, a cor de cada categoria e a porcentagem em relação ao total de incidentes.

const GraficosIncidentes = (props) => {
    const [chartData, setChartData] = useState({});

    const chart = () => {
        let empSal = [];
        let empAge = [];
        axios
          .get("https://dev.atmansystems.com/attlas-public/api/Incidents")
          .then(res => {
            console.log('OBJETIVO', res.data);
            for (const dataObj of res.data) {
              empSal.push(parseInt(dataObj.category.length));
              empAge.push((dataObj.category));
              console.log('tem alguma coisa aqui', empSal)
            }
            setChartData({
              labels: empAge,
              datasets: [
                {
                  label: "level of thiccness",
                  data: empSal,
                  backgroundColor: ["rgba(75, 192, 192, 0.6)"],
                  borderWidth: 4
                }
              ]
            });
          })
          .catch(err => {
            console.log(err);
          });
        console.log(empSal, empAge);
      }

      useEffect(() => {
        chart();
      }, []);


        return(
             <>
                <Card3>
                <Doughnut
          data={chartData}
          options={{
            responsive: true,
            title: { text: "THICCNESS SCALE", display: true },
            scales: {
              yAxes: [
                {
                  ticks: {
                    autoSkip: true,
                    maxTicksLimit: 10,
                    beginAtZero: true
                  },
                  gridLines: {
                    display: false
                  }
                }
              ],
              xAxes: [
                {
                  gridLines: {
                    display: false
                  }
                }
              ]
            }
          }}
        />

                </Card3>

            </>
        )
}

export default GraficosIncidentes;
