import axios from "axios"

export const handleIncident = async (setDataIncidents, setGraficos) => {
    axios
      .get("https://dev.atmansystems.com/attlas-public/api/Incidents")
      .then((res) => {
        // alert("ok");
        setDataIncidents(res.data);
        setGraficos(res.data);
      })
      .catch((error) => {
        alert("deu erro");
        console.log(error.response);
      })
  }

  export const handleControlers = async (setDataControlers) => {
    axios
      .get("https://dev.atmansystems.com/attlas-public/api/Controllers")
      .then((res) => {
        alert("ok");
        setDataControlers(res.data);
      })
      .catch((error) => {
        alert("deu erro");
        console.log(error.response);
      });
  };
