import React, { Component, useState } from "react";
import { Map, TileLayer, Popup, Marker } from "react-leaflet";
import green from "../assets/leaf-green.png";
import leafGreen from "../assets/leaf-green.png";
import leafRed from "../assets/leaf-red.png";
import leafOrange from "../assets/leaf-orange.png";
import leafShadow from "../assets/leaf-shadow.png";
import localizador from '../assets/marcador.png';
import L from "leaflet";
import Acidentes from "../Pages/Acidentes";

function MyMap() {
  const [center, setCenter] = useState({ lat: -20.30297, lng: -40.303538 });
  const [teste, setteste] = useState({ lat: -20.3021, lng: -40.30356 });
  const [data, setData] = useState([]);

  const handleIncident = () => {
    axios
      .get("https://dev.atmansystems.com/attlas-public/api/Incidents")
      .then((res) => {
        // alert("ok");
        console.log(res.data);
        setData(res.data);
      })
      .catch((error) => {
        alert("deu erro");
        console.log(error.response);
      });
  };

  useEffect(() => {
    handleIncident();
  });


  const grenIcon = L.icon({
    iconUrl: localizador,
    // shadowUrl: leafShadow,
    iconSize: [50, 50], // size of the icon
    shadowSize: [50, 64], // size of the shadow
    iconAnchor: [22, 94], // point of the icon which will correspond to marker's location
    shadowAnchor: [4, 62], // the same for the shadow
    popupAnchor: [-3, -76],
  });

  const positionIcon = [center.lat, center.lng];

  return (
    <div>
        <Acidentes/>

      <Map
        className="map"
        center={center}
        zoom={30}
        // style={{ height: 500, width: "100%" }}
      >
        <Marker position={positionIcon} icon={grenIcon}>
          <Popup>Estou aqui</Popup>

        </Marker>

        <Marker position={teste}>
          <Popup>teste 2</Popup>
        </Marker>


        <TileLayer
          attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
      </Map>
    </div>
  );
}

export default MyMap;
