import axios from "axios"

export const handleIncident = async (setDataIncidents, setGraficos) => {
    axios
      .get("https://dev.atmansystems.com/attlas-public/api/Incidents")
      .then((res) => {
        // alert("ok");
        setDataIncidents(res.data);
        setGraficos(res.data);
      })
      .catch((error) => {
        alert("deu erro");
        console.log(error.response);
      })
  }

  export const handleControlers = async (setDataControlers) => {
    axios
      .get("https://dev.atmansystems.com/attlas-public/api/Controllers")
      .then((res) => {
        alert("ok");
        setDataControlers(res.data);
      })
      .catch((error) => {
        alert("deu erro");
        console.log(error.response);
      });
  };

  const chart = () => {
    let empSal = [];
    let empAge = [];
    axios
      .get("https://dev.atmansystems.com/attlas-public/api/Incidents")
      .then((res) => {
        console.log("OBJETIVO", res.data);
        for (const dataObj of res.data) {
          empSal.push(parseInt(dataObj.category.length));
          empAge.push(dataObj.category);
          console.log("tem alguma coisa aqui", empSal);
        }
        setChartData({
          labels: empAge,
          datasets: [
            {
              data: empSal,
              backgroundColor: ["rgba(100, 192, 192, 0.6)"],
              borderWidth: 3,
            },
          ],
        });
      })
      .catch((err) => {
        console.log(err);
      });
    console.log(empSal, empAge);
  };
