import React, { Component, useEffect, useState } from "react";
import { Map, TileLayer, Popup, Marker } from "react-leaflet";
import green from "../assets/leaf-green.png";
import leafGreen from "../assets/leaf-green.png";
import leafRed from "../assets/leaf-red.png";
import leafOrange from "../assets/leaf-orange.png";
import leafShadow from "../assets/leaf-shadow.png";
import localizador from "../assets/marcador.png";
import semaforo from '../assets/semaforo.png';
import L from "leaflet";
import axios from "axios";
import Acidentes from '../Pages/Acidentes';

function MyMap(props) {
  const [center, setCenter] = useState({ lat: -20.30297, lng: -40.303538 });
  const [data, setData] = useState([]);
  const [controlers, setControlers] = useState([])

  const handleIncident = async () => {
    axios
      .get("https://dev.atmansystems.com/attlas-public/api/Incidents")
      .then((res) => {
        // alert("ok");
        setData(res.data);

      })
      .catch((error) => {
        alert("deu erro");
        console.log(error.response);
      });
  }

  const handleControlers = async () => {
    axios
    .get("https://dev.atmansystems.com/attlas-public/api/Controllers")
    .then((res) => {
      alert("ok");
      setControlers(res.data);
    })
    .catch((error) => {
      alert("deu erro");
      console.log(error.response);
    });
  }

  useEffect(() => {
    handleIncident();
    handleControlers();
  }, []);

  console.log("DADOS", data);

  const marcador = L.icon({
    iconUrl: localizador,
    // shadowUrl: leafShadow,
    iconSize: [50, 50], // size of the icon
    shadowSize: [50, 64], // size of the shadow
    iconAnchor: [22, 94], // point of the icon which will correspond to marker's location
    shadowAnchor: [4, 62], // the same for the shadow
    popupAnchor: [-3, -76],
  })

  const controladores = L.icon({
    iconUrl: semaforo,
    // shadowUrl: leafShadow,
    iconSize: [50, 50], // size of the icon
    shadowSize: [50, 64], // size of the shadow
    iconAnchor: [22, 94], // point of the icon which will correspond to marker's location
    shadowAnchor: [4, 62], // the same for the shadow
    popupAnchor: [-3, -76],
  })

  //   const teste = {lat: data[0].latitude, lng: data[0].longitude}

  return (
    <div>
      <Map
        className="map"
        center={center}
        zoom={100}
        // style={{ height: 500, width: "100%" }}
      >
        {data.length > 0 &&
          data.map((p) => {
            const positionMark = { lat: p.latitude, lng: p.longitude };
            return (
              <div>
                <Marker position={positionMark} icon={marcador}>
                  <Popup>INCIDIDENTES</Popup>
                </Marker>
              </div>
            );
          })}

          {controlers.length > 0 &&
          controlers.map((p) => {
            const positionMark = { lat: p.latitude, lng: p.longitude };
            const id = p.id   

            return (
            <>
              <div>
                <Marker position={positionMark} icon={controladores}>
                  <Popup>CONTROLADORES</Popup>
                  <Popup>ID: {id}</Popup>
                </Marker>
                  <Acidentes
                    name={p.name}
                    description={p.description}
                    id={p.id}
                    posicao={p.latitude}
                  />
              </div>

             </> 
            )
          })}



        <TileLayer
          attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
      </Map>


    </div>
  );
}

export default MyMap;
